import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Link, Navigate } from "react-router-dom";
import AuthContext from "./components/AuthContext";
import { useParams } from "react-router-dom";

function App() {
  const [auth, setAuth] = useState({
    username: "",
    password: "",
    isLoggedIn: false,
  });

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    const storedPassword = localStorage.getItem("password");
    if (storedUsername && storedPassword) {
      setAuth({ ...auth, isLoggedIn: true });
    }
  }, []);

  const handleSignIn = (e) => {
    e.preventDefault();
    if (
      auth.username.length === 5 &&
      auth.password.length === 8 &&
      /[a-zA-Z]/.test(auth.password) &&
      /[!@#$%^&*]/.test(auth.password)
    ) {
      localStorage.setItem("username", auth.username);
      localStorage.setItem("password", auth.password);
      setAuth({ ...auth, isLoggedIn: true });
    } else {
      alert("Invalid username or password");
    }
  };

  const handleSignOut = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("password");
    setAuth({ ...auth, isLoggedIn: false });
  };

  return (
    <AuthContext.Provider value={{ auth, setAuth }}>
      <BrowserRouter>
        <Routes>
          <Route
            path="/"
            element={<SignInForm auth={auth} handleSignIn={handleSignIn} />}
          />
          <Route
            path="/books"
            element={auth.isLoggedIn ? <BookList /> : <Navigate to="/" />}
          />
          <Route
            path="/books/:bookId"
            element={auth.isLoggedIn ? <BookSummary /> : <Navigate to="/" />}
          />
        </Routes>
        {auth.isLoggedIn && (
          <div>
            <Link to="/books">Book List</Link>
            <button onClick={handleSignOut}>Sign Out</button>
          </div>
        )}
      </BrowserRouter>
    </AuthContext.Provider>
  );
}

function SignInForm({ auth, handleSignIn }) {
  const handleUsernameChange = (e) => {
    auth.username = e.target.value;
  };

  const handlePasswordChange = (e) => {
    auth.password = e.target.value;
  };

  return (
    <form onSubmit={handleSignIn}>
      <label>Username:</label>
      <input
        type="text"
        value={auth.username}
        onChange={handleUsernameChange}
      />
      <br />
      <label>Password:</label>
      <input
        type="password"
        value={auth.password}
        onChange={handlePasswordChange}
      />
      <br />
      <button type="submit">Sign In</button>
    </form>
  );
}

function BookList() {
  return (
    <div>
      <h1>Book List</h1>
      <ul>
        <li>
          <Link to="/books/1">Book 1</Link>
        </li>
        <li>
          <Link to="/books/2">Book 2</Link>
        </li>
        <li>
          <Link to="/books/3">Book 3</Link>
        </li>
      </ul>
      <button onClick={() => window.history.back()}>Back</button>
    </div>
  );
}

function BookSummary() {
  const params = useParams();
  const bookId = params.bookId;
  return (
    <div>
      <h1>Book {bookId} Summary</h1>
      <p>This is the summary of book {bookId}</p>
      <button onClick={() => window.history.back()}>Back</button>
    </div>
  );
}

export default App;
